const TIME_LIMIT=30;
const int LIDAR_RANGE = 5;
const int HALL_WIDTH = 5;
const int HALL_LENGTH = 20;
const int INIT_SCENARIO = 0;
const int INIT_CAR_DIST_S = 0;
const int INIT_CAR_DIST_F = 15;
const int INIT_CAR_HEADING = 0;

formula shouldCrash1 = (car_dist_f > 5) & (car_dist_s < -2 | car_dist_s > 2); // Crash in the inside of the turn (or outside of fairway)
formula shouldCrash2 = (car_dist_f > INIT_CAR_DIST_F);// Can't go backwards
// formula shouldCrash3 = (car_dist_f > INIT_CAR_DIST_F - 5) & (! (car_heading = 7 | car_heading <= 1)) // Not allowed to turn too much at the start.
formula shouldCrashFront = (scenario=0 | scenario=1) & (car_dist_f=0); // Reached end of track
formula shouldCrashLeftSide = (scenario > 0) & (car_dist_s < -2); // Too far left. Scenario is not left.
formula shouldCrashRightSide = (scenario=0 | scenario=2) & (car_dist_s > 2); // Too far right. Scenario is not right.
formula shouldCrash = shouldCrash1 | shouldCrash2 | shouldCrashFront | shouldCrashLeftSide | shouldCrashRightSide;

formula hallTransitionLeft = (scenario=0) & (car_dist_s=-7) & (car_dist_f<=5) ;
formula hallTransitionRight = (scenario=1) & (car_dist_s=7) & (car_dist_f<=5);
formula hallTransitionStraight = (scenario=2) & (car_dist_f=0) & (car_dist_s <= 2) & (car_dist_s>=-2);
formula hallTransition = (! shouldCrash) & (hallTransitionLeft | hallTransitionRight | hallTransitionStraight);
// n.b. hallTransition is not the exit condition. The exit condition is based on time.

formula exit_condition = (t = TIME_LIMIT) & (err = 0);



// n.b right now we have no control for when car_heading is 4.
// n.b for now we are assuming perfect knowledge of the scenario.
formula controlRight1 = (car_dist_f_est > 5) & (car_heading_est = 3 | car_heading_est = 2); // For when facing very far sideways
formula controlRight2 = (car_dist_f_est > 5) & (car_heading =1) & (car_dist_s_est <= 0); // For when facing diagonal and on left half of track
formula controlRightR = (scenario=1) & (car_dist_f_est <= 5) & (!(car_heading_est=6 | car_heading_est=5 | car_heading_est = 4)); // Makes us turn right after the curve.
formula controlRightS = (scenario=2) & (car_heading_est = 3 | car_heading_est = 2 | car_heading_est=1);
formula controlRightL = (scenario=0) & (car_dist_f_est <= 5) & (car_heading_est=3 | car_heading_est=4); // Correct for oversteer while trying to turn left.
formula controlRight = controlRight1 | controlRight2 | controlRightR | controlRightS | controlRightL;

formula controlLeft1 = (car_dist_f_est > 5) & (car_heading_est = 6 | car_heading_est = 7);
formula controlLeft2 = (car_dist_f_est > 5) & (car_heading =7) & (car_dist_s_est >= 0);
formula controlLeftL = (scenario=0) & (car_dist_f_est <= 5) & (!(car_heading_est = 2 | car_heading_est = 3 | car_heading_est = 4)); //turn left after the curve
formula controlLeftR = (scenario=1) & (car_dist_f_est <= 5) & (car_heading_est=4|car_heading_est=5); // correct for oversteer when trying to turn right.
formula controlLeftS = (scenario=2) & (car_heading_est = 7 | car_heading_est = 6 | car_heading_est=5);
formula controlLeft = (! controlRight) & (controlLeft1 | controlLeft2 | controlLeftL | controlLeftR | controlLeftS);

formula performPerception = ((pc=0) & (! shouldCrash) & (! hallTransition) & (err = 0));

module dynamics
    scenario: [0..2] init INIT_SCENARIO; // 0 is left, 1 is right, 2 is straight.
    err: [0..1] init 0;
    t: [1..TIME_LIMIT] init 1; // Once TIME_LIMIT is reached, we should not update the states anymore.
    car_dist_s: [-8..8] init INIT_CAR_DIST_S; // This is x_distance.  0 is middle
    car_dist_f: [0..16] init INIT_CAR_DIST_F; 
    car_heading: [0..7] init INIT_CAR_HEADING; // Measured in radians, where 0 is forward (decreasing dist_f)

    car_dist_f_est: [0..6] init 6; // 6 means ``6 or more''.
    car_heading_est: [0..7] init 0; 
    car_dist_s_est: [-8..8] init INIT_CAR_DIST_S;

    u: [-1..1] init 0; // Control input. -1 is right and 1 is left.
 
    pc: [0..3] init 0;
    // pc: 0 perception (and bookkeeping for crashing.)
    // pc: 1 controller
    // pc: 2 dynamics 1 (heading)
    // pc: 3 dynamics 2 (movement)
    // Important! At the step between TIME_LIMIT-1 and TIME_LIMIT, we ignore control
    // and just do the bookkeeping to get ready for the next scenario.

 
    [] (pc=0) & (err = 1) -> (err'=1);
    [] (pc=0) & shouldCrash-> (err'=1);
    [] (pc=0) & hallTransition -> (pc'=1);
